    <script>
        $(document).ready(function() {
            // Verificar si hay errores en la sesión
            @if ($errors->any())
                @foreach ($errors->all() as $error)
                    Swal.fire({
                        icon: 'error',
                        title: '¡Error!',
                        text: '{{ $error }}',
                        confirmButtonText: 'Aceptar'
                    });
                @endforeach
            @endif
        
            // Verificar si hay un mensaje de éxito en la sesión
            @if (session('success'))
                Swal.fire({
                    icon: 'success',
                    title: '¡Éxito!',
                    text: '{{ session('success') }}',
                    confirmButtonText: 'Aceptar'
                });
            @endif
        });
    </script>
